package Triangle_Pattern;

public class Inverted_Left_Side_Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int size = 9;
		
		for (int i = 0; i < size; i++) {
			for (int j = i; j < size; j++) {
				System.out.print("* ");
			}
			System.out.println("");
		}
	}

}
